mapper.select:
/* TestMapper.getUsers */
SELECT
    *
FROM T_USER
WHERE 1=1
AND ID = #{id}

mapper.insert:
INSERT INTO T_USER (
    ID,
    USER_EMAIL,
    USER_PWD
) VALUES (
    #{id},
    #{userEmail}
    #{userPwd}
)

mapper.update:
UPDATE T_USER
SET 
    ID = #{id},
    USER_EMAIL = #{userEmail}
WHERE id = #{id}
